
// Function No Args and No Return Value
function one()
{
	console.log("Welcome One")
}
one()
// Function With Args and No Return Value
function two(a,b)
{
	c=a+b
	console.log("addition:" +c)
}
two(10,25)

// Function No Args and With Return Value

function three()
{
	a=5
	b=8
	 return add=a+b
	
}
three()
console.log("addition:" +add)

// Function With Args and With Return Value

function four(a,b)
{
	return a+b
	
}
res=four(2,25)
console.log("addition:" +res)

// Function Variable express function
var s=function(a,b)
{
	return a+b
}
var res=s(45,95)
console.log("addition :"+ res)

// Arrow Function
var s=(a,b)=>
{
	return a+b
}
var res=s(40,95)
console.log("addition :"+ res)

//Fat Arrow Function
((a,b)=>{
	c= a+b
	console.log("addition :"+c)
})(45,55)

